import React, { useState, useEffect  } from 'react';
import { AppBar, Avatar, Toolbar, Button, Typography } from '@material-ui/core';
import { Link, useHistory, useLocation } from 'react-router-dom';
import useStyles from './styles';
import clubmedia from '../../images/clubmedia.png';
import { useDispatch } from 'react-redux';
import * as actionType from '../../constants/actionTypes';
const Navbar = () => {

    const classes = useStyles();
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    const dispatch = useDispatch();
    const history = useHistory();
    const location = useLocation();

    const logout = () => {
      dispatch({ type: actionType.LOGOUT });

      history.push('./auth');

      setUser(null);
    }

    useEffect(() => {
        const token = user?.token;

      ///JWT goes here


      setUser(JSON.parse(localStorage.getItem('profile')));
    }, [location]);

    return(
    <AppBar className={classes.appBar} position="static" color="inherit">
      <div className={classes.brandContainer}>
         <Typography component={Link} to="/" className={classes.heading} variant="h2" align="center">Club Media</Typography>
         <img className={classes.image} src={clubmedia} alt="icon" height="60" />
      </div> 
      <Toolbar className={classes.toolbar}>
        {user ? (
          <div className= { classes.profile}>
            <Avatar className = {classes.purple} alt={user.result.name} src={user.result.imageUrl}>{user.result.name.charAt(0)}</Avatar>
            <Typography className={classes.userName} variant="h6">{user.result.name}</Typography>
            <Button variant="contained" className={classes.logout} color="secondary" onClick={logout}>Logout</Button>

          </div>

        ) : (
             <Button component={Link} to="/auth" variant="contained" color="default">Sign In</Button>
        )}
      </Toolbar>



  </AppBar>
    );
        }


export default Navbar;
